# Define file pathsopcodes_file = 'Opcodes.txt'addresses_file = 'Addresses.txt'output_file = 'PS2_PS4_Patch.lua'# Read Opcodeswith open(opcodes_file, 'r') as file:    opcodes = [line.strip() for line in file]# Read Addresseswith open(addresses_file, 'r') as file:    addresses = [line.strip() for line in file]# Write to output filewith open(output_file, 'w') as file:    file.write('apiRequest(0.1)\n\n')    file.write('local eeObj = getEEObject()\n')    file.write('local emuObj = getEmuObject()\n\n')    file.write('local patcher = function()\n')        for opcode, address in zip(opcodes, addresses):        file.write(f'eeObj.WriteMem32(0x{address}, 0x{opcode})\n')        file.write('end\n')    file.write('emuObj.AddVsyncHook(patcher)\n')print(f'Done. The output file is {output_file}.')